import React from "react";
import axios from "axios";
import { useState } from "react";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import SurahButton from "./SurahButton";
const JuzPage = () => {
  const [ayahs, setAyahs] = useState([]);
  const [translation, setTranslation] = useState([]);
  const [type, setType] = useState([]);
  const [englishN, setEnglishN] = useState([]);
  const [surahs, setSurahs] = useState([]);
  const [number, setNumber] = useState([]);
  const location = useLocation();
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const n = location.state.number;
  const getJuz = () => {
    axios({
      // this page will display the surahs in that juz, but we nees a way to see the api docucmentation. also the buttons on the Juz page dont show
      method: "GET",
      url: `http://api.alquran.cloud/v1/juz/${n}`,
    })
      .then((response) => {
        console.log(response.data.data);
        setSurahs(response.data.data.surahs);
        console.log("juz");
        console.log(surahs);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getJuz();
    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return (
    <div>
      {surahs.map((surah, i) => (
        <SurahButton
          onClick={() => {
            navigate("surah", { state: { surah: surah, number: number[i] } });
          }}
          surah={surah}
          type={type[i]}
          eName={englishN[i]}
          translation={translation[i]}
          ayahs={ayahs[i]}
          key={i}
          number={number[i]}
        />
      ))}
    </div>
  ); // this will map out all of the surahs in that juz based on the use state containing those surahs
};

export default JuzPage;
