import React from "react";
//have array of 604, the do map and set the array state with that specific page to get the values
// set a state to be array holding value of ayahs for each page of the quran.
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import "./surahPage.css";
import Autocomplete from "react-autocomplete";
import Quraa from "./Quraa";
const SurahPage = () => {
  const location = useLocation();
  const [loading, setLoading] = useState(true);
  const surah = location.state.number;
  const surahName = location.state.name;
  const [fullS, setFullS] = useState([]);
  const [value, setValue] = useState();
  const [edition, setEdition] = useState([]);
  const [arabic, setArabic] = useState();
  const [sheikh, setSheikh] = useState();
  const [num, setNum] = useState([]);
  const getSurah = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/surah/${surah}`,
    })
      .then((response) => {
        setNum(response.data.data.ayahs.map((a) => a.number));
        setFullS(response.data.data.ayahs.map((a) => a.text));
        console.log(response.data.data.ayahs);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const getAudio = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/edition?format=audio&language=ar&type=versebyverse`,
    })
      .then((response) => {
        console.log("edition");
        console.log(response.data.data);
        if (arabic) {
          setEdition(
            response.data.data.map((name, i) => ({
              edition: name.identifier,
              label: name.name,
              index: i,
            }))
          );
        } else {
          setEdition(
            response.data.data.map((name, i) => ({
              edition: name.identifier,
              label: name.englishName,
              index: i,
            }))
          );
        }
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getAudio();
    getSurah();
    loading ? setLoading(false) : console.log("lol");
  }, [loading, arabic]);
  return (
    <div className="ayah">
      <div>
        <button
          onClick={() => {
            setArabic(true);
          }}
        >
          Arabic
        </button>
        <button
          onClick={() => {
            setArabic(false);
          }}
        >
          English
        </button>
      </div>
      <Autocomplete
        menuStyle={{ zIndex: 0.5 }}
        className="AutocompleteSurah"
        getItemValue={(item) => item.label}
        items={edition}
        renderItem={(item, isHighlighted) => (
          <div
            style={{
              background: isHighlighted ? "lightgray" : "white",
              zIndex: 1,
            }}
          >
            {item.label}
          </div>
        )}
        value={value}
        onChange={(e) => setValue(e.target.value)}
        onSelect={(val) => {
          setValue(val);
          console.log(val);
        }}
      />
      <button
        onClick={() => {
          setSheikh(edition.find((surah) => surah.label === value).edition);
          console.log("sheikh");
          console.log(sheikh);
        }}
      >
        Set Sheikh
      </button>
      <audio
        controls
        src={`https://cdn.islamic.network/quran/audio-surah/128/${sheikh}/${surah}.mp3`}
        className="aud"
      >
        Play entire Surah
      </audio>
      {surahName}
      {fullS.map((a, i) => (
        <div className="inner">
          <audio
            controls
            src={`https://cdn.islamic.network/quran/audio/${Quraa[sheikh]}/${sheikh}/${num[i]}.mp3`}
            className="aud"
          />
          <div key={i}>
            {a}
            {i + 1}۝
            <br />
          </div>
        </div>
      ))}
      صَدَقَ اللّٰهُ الْعَظِيْم
    </div>
  );
};

export default SurahPage;
