import React from "react";
import axios from "axios";

const JuzPage = () => {
  const location = useLocation();
  const [surahs, setSurahs] = useState([]);
  const [loading, setLoading] = useState(true);
  const number = location.state.number;
  const getJuz = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/juz/${number}`, // this isnt the real endpoint. how do i map out all 30 juz when i can only get 1 juz at a time? its set to 3 rn
    })
      .then((response) => {
        setSurahs(response.data.data.surahs);
        console.log("juz");
        console.log(response.data.data.surahs);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getJuz();
    // getInfo();
    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return <div></div>;
};

export default JuzPage;
