import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
const Home = () => {
  const [surahs, setSurahs] = useState();
  const [sArray, setSArray] = useState([]);
  const [loading, setLoading] = useState(true);
  const getSurahs = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/surah`,
    })
      .then((response) => {
        //console.log(response.data.data.map((surah) => surah.name));
        setSurahs(response.data.data.map((surah) => setSArray.add(surah.name)));
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getSurahs();
    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return <div>{surahs}</div>;
};

export default Home;
