import React from "react";
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import "./surahPage.css";
import Audio from "./Audio";
import Autocomplete from "react-autocomplete";
const SurahPage = () => {
  const location = useLocation();
  const [sound, SetSound] = useState();
  const [loading, setLoading] = useState(true);
  console.log(location);
  const surah = location.state.number;
  const [fullS, setFullS] = useState([]);
  const [value, setValue] = useState();
  const [edition, setEdition] = useState([]);
  const [person, setPerson] = useState();
  const [arabic, setArabic] = useState();
  const [sheikh, setSheikh] = useState();
  const [ayahN, setAyahN] = useState();
  const getSound = () => {
    axios({
      method: "GET",
      url: `https://cdn.islamic.network/quran/audio/32/${edition}/${32}.mp3`,
    })
      .then((response) => {
        SetSound(response.data.data);
        console.log(response.data.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const getSurah = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/surah/${surah}`,
    })
      .then((response) => {
        setFullS(response.data.data.ayahs.map((a) => a.text));
        console.log(response.data.data.ayahs);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const getAudio = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/edition?format=audio&language=ar&type=versebyverse`,
    })
      .then((response) => {
        //setEdition(response.data.data);
        console.log("edition");
        console.log(response.data.data);
        if (arabic) {
          setEdition(
            response.data.data.map((name, i) => ({
              edition: name.identifier,
              label: name.name,
              index: i,
            }))
          );
        } else {
          setEdition(
            response.data.data.map((name, i) => ({
              edition: name.identifier,
              label: name.englishName,
              index: i,
            }))
          );
        }
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getAudio();
    getSurah();
    loading ? setLoading(false) : console.log("lol");
  }, [loading, arabic]);
  return (
    <div className="ayah">
      <div>
        <button
          onClick={() => {
            setArabic(true);
          }}
        >
          Arabic
        </button>
        <button
          onClick={() => {
            setArabic(false);
          }}
        >
          English
        </button>
      </div>
      <Autocomplete
        className="Autocomplete"
        getItemValue={(item) => item.label}
        items={edition}
        renderItem={(item, isHighlighted) => (
          <div style={{ background: isHighlighted ? "lightgray" : "white" }}>
            {item.label}
          </div>
        )}
        value={value}
        onChange={(e) => setValue(e.target.value)}
        onSelect={(val) => {
          setValue(val);
          console.log(val);
        }}
      />
      <button
        onClick={() => {
          setSheikh(edition.find((surah) => surah.label === value).edition);
          console.log("sheikh");
          console.log(sheikh);
        }}
      >
        Play Audio
      </button>
      {fullS.map((a, i) => (
        <div className="inner">
          <div key={i}>
            {" "}
            {/*have the onlclick thing here in the div attributes*/}
            {a}
            {i + 1}۝
            <br />
          </div>
        </div>
      ))}
      صَدَقَ اللّٰهُ الْعَظِيْم
    </div>
  );
};

export default SurahPage;
