import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import SurahButton from "../componenets/SurahButton";
const Home = () => {
  const [surahs, setSurahs] = useState([]);
  //   const [sArray, setSArray] = useState([]);
  let sArray = [];
  const [loading, setLoading] = useState(true);
  const getSurahs = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/surah`,
    })
      .then((response) => {
        //console.log(response.data.data.map((surah) => surah.name));
        setSurahs(response.data.data.map((surah) => surah.name));
        // console.log(surahs);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getSurahs();
    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return (
    <div>
      {surahs.map((surah) => (
        <div className="button">
          <SurahButton onClick={() => {}} surah={surah} />
        </div>
      ))}
    </div>
  );
};

export default Home;
