import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";
import GetJuz from "../componenets/GetJuz";
import { Navigate, useNavigate } from "react-router-dom";
//right now, this is supposed to map 30 buttons, whos prop is the juz number.
const Juz = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [juz, setJuz] = useState([]);
  const getJuz = () => {
    //setJuz([]);
    for (let i = 1; i <= 30; i++) {
      axios({
        // this page will display the surahs in that juz, but we nees a way to see the api docucmentation. also the buttons on the Juz page dont show
        method: "GET",
        url: `http://api.alquran.cloud/v1/juz/${i}/quran-uthmani`,
      })
        .then((response) => {
          setJuz(() => {
            juz.push(response.data.data.surahs);
          });
          console.log(response.data.data.surahs);
          console.log("real" + juz);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  };
  useEffect(() => {
    getJuz();
    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return (
    <div>
      {/* {() => {
        for (let i = 1; i <= 30; i++) {
          return (
            <button
              onClick={() => navigate("/JuzPage", { state: { number: i } })} // for somereason tha buttons arent getting mapped
            >
              juz {i}
            </button>
          );
        }
      }} */}
    </div>
  );
};

export default Juz;
