import React from "react";
import axios from "axios";
import { useState } from "react";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
const JuzPage = () => {
  const location = useLocation();
  const [surahs, setSurahs] = useState([]);
  const [loading, setLoading] = useState(true);
  const n = location.state.number;
  const getJuz = () => {
    axios({
      // this page will display the surahs in that juz, but we nees a way to see the api docucmentation. also the buttons on the Juz page dont show
      method: "GET",
      //   url: `http://api.alquran.cloud/v1/juz/${n}`,
      url: `http://api.alquran.cloud/v1/juz/3/quran-uthmani`,
    })
      .then((response) => {
        console.log(response.data.data);
        setSurahs(response.data.data.surahs);
        console.log("juz");
        console.log(surahs);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getJuz();
    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return <div></div>; // this will map out all of the surahs in that juz based on the use state containing those surahs
};

export default JuzPage;
