import React from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./rec.css";
import Autocomplete from "react-autocomplete";
const Reccomendations = () => {
  const navigate = useNavigate();
  const [value, setValue] = useState();
  return (
    <div className="recP">
      <div className="lSide">
        <div className="weekly">
          weekly
          <br />
          <button
            onClick={() => {
              navigate("/surah", { state: { surah: "Al-Kahf", number: 18 } });
            }}
            className="kB"
          >
            Kahf
          </button>
        </div>
        <div className="aSearch">
          Search
          <br />
          <Autocomplete
            className="Autocomplete"
            getItemValue={(item) => item.label}
            renderItem={(item, isHighlighted) => (
              <div
                style={{ background: isHighlighted ? "lightgray" : "white" }}
              >
                {item.label}
              </div>
            )}
            onChange={(e) => setValue(e.target.value)}
            onSelect={(val) => {
              setValue(val);
              console.log(val);
            }}
          />
          <button
            className="kA"
            onClick={() => {
              navigate("/AyahPage", {
                state: { ayahNum: value, desc: `Ayah ${value}` },
              });
            }}
          >
            Kursi
          </button>
        </div>
      </div>

      <div className="daily">
        <span>Daily</span>
        <br />
        <button
          onClick={() => {
            navigate("/surah", { state: { surah: "Al-Fatihah", number: 1 } });
          }}
          className="fS"
        >
          Al-Fatihah
        </button>
        <br />
        <button
          className="kA"
          onClick={() => {
            navigate("/AyahPage", {
              state: { ayahNum: 262, desc: "Ayatul Kursi" },
            });
          }}
        >
          Kursi
        </button>

        <br />
        <button
          onClick={() => {
            navigate("/surah", { state: { surah: "Al-Ikhlas", number: 112 } });
          }}
          className="iS"
        >
          Al Ikhlas
        </button>
        <br />
        <button
          onClick={() => {
            navigate("/surah", { state: { surah: "Al-Falaq", number: 113 } });
          }}
          className="fS"
        >
          Al Falaq
        </button>
        <br />
        <button
          onClick={() => {
            navigate("/surah", { state: { surah: "Al-Naas", number: 114 } });
          }}
          className="nS"
        >
          Al Naas
        </button>
        <br />
        <button
          onClick={() => {
            navigate("/surah", { state: { surah: "Al-Mulk", number: 67 } });
          }}
          className="mS"
        >
          Al Mulk
        </button>
        <br />
        <button
          onClick={() => {
            navigate("/surah", { state: { surah: "Al-Kafirun", number: 109 } });
          }}
          className="kS"
        >
          Al Kafirun
        </button>
        <br />
        <button
          onClick={() => {
            navigate("/surah", { state: { surah: "Al-Waqi’ah", number: 56 } });
          }}
          className="wS"
        >
          Al-Waqi’ah
        </button>
      </div>
    </div>
  );
};

export default Reccomendations;
