      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getSurahs();
    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return (
    <div>
      {surahs.map((surah) => {
        <SurahButton onClick={() => {}} surah={surah} />;
        console.log(surah);
      })}
      <SurahButton onClick={() => {}} surah={"hi"} />
    </div>
  );
};

export default Home;
