import React from "react";
import { useState } from "react";
import Autocomplete from "react-autocomplete";
import { useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
const Search = () => {
  const [search, setSearch] = useState([]);
  const [loading, setLoading] = useState(true); // values are not being passed to surahPage in teh /surah navigation
  const [value, setValue] = useState(); // also the language buttons are not working
  const [arabic, setArabic] = useState();
  const [indexing, setIndexing] = useState([]);
  const navigate = useNavigate();
  const [number, setNumber] = useState();
  const [index, setIndex] = useState([]);
  const getSearch = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/surah`,
    })
      .then((response) => {
        if (arabic) {
          setSearch(
            response.data.data.map((surah) => ({
              label: surah.name,
            }))
          );
          setIndexing(
            response.data.data.map((surah, i) => ({
              surah: surah.name,
              index: i,
            }))
          );
        } else {
          setSearch(
            response.data.data.map((surah) => ({
              label: surah.englishName,
            }))
          );
          setIndexing(
            response.data.data.map((surah, i) => ({
              surah: surah.name,
              index: i,
            }))
          );
        }
      })

      .catch((error) => {
        console.log(error);
      });
  };
  const i = () => {
    setIndex(
      indexing.map((k, i) => {
        if (k.surah === value) {
          return i;
        }
      })
    );
  };
  useEffect(() => {
    i();
    getSearch();

    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return (
    <div>
      <button
        onClick={() => {
          setArabic(true);
          getSearch();
        }}
      >
        Search in Arabic
      </button>
      <button
        onClick={() => {
          setArabic(true);
          getSearch();
        }}
      >
        Search in English
      </button>

      <Autocomplete
        getItemValue={(item) => item.label}
        items={search}
        renderItem={(item, isHighlighted) => (
          <div style={{ background: isHighlighted ? "lightgray" : "white" }}>
            {item.label}
          </div>
        )}
        value={value}
        onChange={(e) => setValue(e.target.value)}
        onSelect={(val) => setValue(val)}
      />
      {/* {setIndex(
        indexing.map((k, i) => {
          if (k.surah === value) {
            return i;
          }
        })
      )} */}
      <button
        onClick={() =>
          navigate("/surah", { state: { surah: value, number: index } })
        }
      >
        Search
      </button>
    </div>
  );
};

export default Search;
