import React from "react";
import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
const SurahPage = (props) => {
  const location = useLocation();
  const [loading, setLoading] = useState(true);
  const surah = location.state.number;
  const [fullS, setFullS] = useState();
  const getSurah = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/surah/${surah}`,
    })
      .then((response) => {
        console.log(response.data.data.ayahs);
        setFullS(response.data.data.ayahs);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getSurah();
    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return (
    <div>
      {fullS.map((surah, i) => (
        <div key={i}>{surah}</div>
      ))}{" "}
    </div>
  );
};

export default SurahPage;
