import React from "react";
import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "./surahPage.css";
const SurahPage = (props) => {
  const location = useLocation();
  const [loading, setLoading] = useState(true);
  const surah = location.state.number;
  const [fullS, setFullS] = useState([]);
  const [info, setInfo] = useState([]);
  const [number, setNumber] = useState([]);
  //   const getInfo = () => {
  //     axios({
  //       method: "GET",
  //       url: `http://api.alquran.cloud/v1/meta`,
  //     })
  //       .then((response) => {
  //         console.log(response.data.data);
  //         console.log("info");
  //         console.log(info);
  //       })
  //       .catch((error) => {
  //         console.log(error);
  //       });
  //   };
  const getSurah = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/surah/${surah}`,
    })
      .then((response) => {
        setFullS(response.data.data.ayahs.map((a) => a.text));
        setNumber(response.data.data.ayahs.map((a) => a.number));
        console.log("t");
        console.log(fullS);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getSurah();
    // getInfo();
    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return (
    <div className="ayah">
      {fullS.map((a, i) => (
        <div className="inner">
          <div key={i}>
            {a}€ù{number[i]}
            <br />
          </div>
        </div>
      ))}
    </div>
  );
};

export default SurahPage;
