import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import "./Home.css";
import SurahButton from "../componenets/SurahButton";
const Home = () => {
  const [hisb, setHisb] = useState([]);
  const [type, setType] = useState([]);
  const [surahs, setSurahs] = useState([]);
  //   const [sArray, setSArray] = useState([]);
  let sArray = [];
  const [loading, setLoading] = useState(true);
  const getHisb = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/meta`,
    })
      .then((response) => {
        //console.log(response.data.data.map((surah) => surah.name));
        //console.log(response.data.data.);
        // console.log(surahs);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const getSurahs = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/surah`,
    })
      .then((response) => {
        console.log(response.data.data.map((surah) => surah));
        setSurahs(response.data.data.map((surah) => surah.name));
        setType(response.data.data.map((surah) => surah.revelationType));
        // console.log(surahs);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getSurahs();
    getHisb();
    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return (
    <div className="main">
      {surahs.map((surah, i) => (
        <SurahButton onClick={() => {}} surah={surah} type={type[i]} />
      ))}
    </div>
  );
};

export default Home;
