import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";
const GetJuz = (props) => {
  const [juz, setJuz] = useState([]);
  const [loading, setLoading] = useState(true);
  const number = props.number;
  const getJuz = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/juz/${number}`, // this isnt the real endpoint. how do i map out all 30 juz when i can only get 1 juz at a time? its set to 3 rn
    })
      .then((response) => {
        setJuz(response.data.data);
        console.log("juz");
        console.log(response.data.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getJuz();
    // getInfo();
    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return <div></div>;
};

export default GetJuz;
