import React from "react";
import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
const SurahPage = (props) => {
  const location = useLocation();
  const [loading, setLoading] = useState(true);
  const surah = location.state.number;
  const [fullS, setFullS] = useState([]);
  const getSurah = () => {
    axios({
      method: "GET",
      url: `http://api.alquran.cloud/v1/surah/${surah}`,
    })
      .then((response) => {
        console.log(response.data.data.ayahs.map((a) => a[0]));
        console.log(response.data.data.ayahs);
        setFullS(response.data.data.ayahs); // this gets the specific ayah. what do i do? i want all of the ayahs, also what are those console errors. do i map all the ayash?
        console.log("t");
        console.log(fullS); // howcome it logs the ayahs array but when i log the useState, it doesnt show the ayahs array.
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    getSurah();
    loading ? setLoading(false) : console.log("lol");
  }, [loading]);
  return (
    <div>
      {fullS.map((a, i) => (
        <div key={i}>{a}</div>
      ))}
    </div>
  );
};

export default SurahPage;
